// Función para esperar un tiempo determinado (ms)
function delay(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

const items = $input.all();
const totalItems = items.length;

console.log(`Iniciando proceso con ${totalItems} items...`);

for (let i = 0; i < totalItems; i++) {
  const item = items[i];
  const id = item.json.id_manychat;
  const ocupacion = item.json.Eliminar_dolor_para;
  let tag = null;

  // Determinamos el tag según las condiciones
  if (/crear/.test(ocupacion)) {
    tag = "GR2-PARACREAR";
  } else if (/familia/.test(ocupacion)) {
    tag = "GR1-FAMILIAYAMIGOS";
  } else if (/mismo/.test(ocupacion)) {
    tag = "GR3-PARATIMISMO";
  } 

  if (tag) {
    // Esperamos para respetar el límite de 10 RPS (aprox. 110 ms)
    await delay(110);

    const options = {
      method: 'POST',
      url: 'https://api.manychat.com/fb/subscriber/addTagByName',
      headers: {
        "Authorization": "Bearer 288702267651723:f96a1f26344892df99c98292741de8c9",
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: {
         subscriber_id: id,
         tag_name: tag
      },
      json: true,
    };

    try {
      const response = await this.helpers.request(options);
      item.json.manychatResponse = response;
      console.log(`Proceso ${i + 1} de ${totalItems}: id: ${id} estado: completado`);
    } catch (error) {
      console.log(`Proceso ${i + 1} de ${totalItems}: id: ${id} estado: error`);
    }
  } else {
    console.log(`Proceso ${i + 1} de ${totalItems}: id: ${id} estado: sin asignación`);
  }
}

console.log("Proceso completado. ¡Todos los items han sido procesados!");
return items;

