return facebookEvent;

return items.map(item => {
  const whatsapp = item.json.whatsapp || ""; // Verifica si existe el campo WhatsApp
  const email = item.json.email || ""; // Verifica si existe el campo email

  if (whatsapp && email) {
    // Si existen ambos campos, retorna un JSON con ambos valores
    return {
      json: {
        whatsapp: whatsapp,
        email: email
      }
    };
  } else if (whatsapp) {
    // Si existe solo WhatsApp, genera un filtro con regex para los √∫ltimos 6 caracteres
    return {
      json: {
        whatsapp: {
          "$regex": `.*${whatsapp.slice(-6)}.*`,
          "$options": "i"
        }
      }
    };
  } else if (email) {
    // Si no existe WhatsApp, utiliza el correo electr√≥nico para generar el filtro
    return {
      json: {
        email: email
      }
    };
  } else {
    // Si no hay datos de WhatsApp ni correo, lanza un error
    throw new Error("No se proporcionaron datos de WhatsApp ni correo electr√≥nico.");
  }
});

let answersJsonOptions = {};
