const crypto = require('crypto-js'); // Asegúrate de que la librería está disponible

const item = $input.first().json;

function hashSHA256(value) {
  return crypto.SHA256(value).toString(crypto.enc.Hex);
}

const encryptedData = {
  em: [hashSHA256(item.email)],  // Correo electrónico
  ph: [hashSHA256(Number(item.whatsapp))],  // Número de teléfono
  country: [hashSHA256(item.country)],  // País
  zp: [hashSHA256(item.zip)], //zip
  ct: [hashSHA256(item.city)], //ciudad
  st: [hashSHA256(item.regionName)], //estado
  // Datos no cifrados deben estar directamente accesibles
  client_ip_address: item.query,
  client_user_agent: item.userAgent,
  fbc: item.fbc,
  fbp: item.fbp,
};

const facebookEvent = {
  data: [
    {
      event_name: "QLEAD",
      event_time: Math.floor(Date.now() / 1000), // Tiempo actual en segundos
      action_source: "website",
      original_event_data: {
        event_name: "QLEAD",
        event_time: Math.floor(Date.now() / 1000)
      },
      user_data: encryptedData,
    }
  ]
};

return facebookEvent;

return items.map(item => {
  const whatsapp = item.json.whatsapp || ""; // Verifica si existe el campo WhatsApp
  const email = item.json.email || ""; // Verifica si existe el campo email

  if (whatsapp && email) {
    // Si existen ambos campos, retorna un JSON con ambos valores
    return {
      json: {
        whatsapp: whatsapp,
        email: email
      }
    };
  } else if (whatsapp) {
    // Si existe solo WhatsApp, genera un filtro con regex para los últimos 6 caracteres
    return {
      json: {
        whatsapp: {
          "$regex": `.*${whatsapp.slice(-6)}.*`,
          "$options": "i"
        }
      }
    };
  } else if (email) {
    // Si no existe WhatsApp, utiliza el correo electrónico para generar el filtro
    return {
      json: {
        email: email
      }
    };
  } else {
    // Si no hay datos de WhatsApp ni correo, lanza un error
    throw new Error("No se proporcionaron datos de WhatsApp ni correo electrónico.");
  }
});

let answersJsonOptions = {};
const inputData = $input.first(); // Obtener el primer item de entrada
const rawBody = inputData.json.body; // Acceder al cuerpo de la solicitud (común en webhooks)

console.log("Datos de entrada recibidos (rawBody):", JSON.stringify(rawBody, null, 2));

try {
  let potentialJsonString = null;

  if (typeof rawBody === 'string') {
    potentialJsonString = rawBody;
    console.log("Input body es un string.");
  } else if (typeof rawBody === 'object' && rawBody !== null) {
    if (typeof rawBody.answersJsonOptions === 'string') {
      potentialJsonString = rawBody.answersJsonOptions;
      console.log("Input body es objeto, usando 'answersJsonOptions' como string.");
    } else if (typeof rawBody.answersJsonOptions === 'object' && rawBody.answersJsonOptions !== null) {
      answersJsonOptions = rawBody.answersJsonOptions;
      console.log("Input body es objeto, usando 'answersJsonOptions' como objeto.");
    } else {
      answersJsonOptions = rawBody;
       console.log("Input body es objeto, usándolo directamente como contenedor de respuestas.");
    }
  } else {
     console.log("Formato de 'body' no procesable directamente:", rawBody);
     // Intentar buscar en la raíz del json de entrada si no está en 'body'
     // *** CORRECCIÓN AQUÍ: Usar 'q1' para la comprobación, no 'q1_edad' ***
     if (typeof inputData.json.q1 !== 'undefined') { // Comprobar si q1 existe en la raíz
        answersJsonOptions = inputData.json;
        console.log("Respuestas encontradas en la raíz del JSON de entrada.");
     } else {
        throw new Error("Formato de entrada no reconocido o respuestas no encontradas en 'body' ni en la raíz.");
     }
  }

  if (potentialJsonString !== null) {
     console.log("Intentando parsear string JSON:", potentialJsonString);
     // *** CORRECCIÓN AQUÍ: Asegurar que el parseo maneje casos donde el string no sea un objeto JSON válido ***
     try {
       answersJsonOptions = JSON.parse(potentialJsonString);
     } catch (parseError) {
       // Intentar verificar si el string ya contiene las claves directamente (menos común)
       if (potentialJsonString.includes('"q1":')) {
          console.warn("El string no era JSON válido, pero parece contener claves. Intentando extraer.");
          // Lógica simple de extracción si es necesario (más compleja y frágil)
          // Por ahora, lanzaremos error si el parseo falla
          throw new Error(`Error al parsear JSON string: ${parseError.message}`);
       } else {
          throw new Error(`El string recibido no es un JSON válido ni contiene claves reconocibles: ${parseError.message}`);
       }
     }
  }

  if (typeof answersJsonOptions !== 'object' || answersJsonOptions === null) {
      throw new Error(`El resultado del parseo no es un objeto válido. Tipo: ${typeof answersJsonOptions}`);
  }
  console.log("Respuestas parseadas y listas para usar:", JSON.stringify(answersJsonOptions, null, 2));

} catch (error) {
  console.error("Error crítico al procesar las respuestas:", error);
  return [{ json: { error: `Error procesando JSON de entrada: ${error.message}` } }];
}

const q1_respuesta = (answersJsonOptions["q1"] || "").toString().trim();
const q2_respuesta = (answersJsonOptions["q2"] || "").toString().trim();
const q3_respuesta = (answersJsonOptions["q3"] || "").toString().trim(); // Capital Listo
const q4_respuesta = (answersJsonOptions["q4"] || "").toString().trim(); // Objetivo
const q5_respuesta = (answersJsonOptions["q5"] || "").toString().trim(); // Ingresos

// Depuración: Mostrar valores extraídos
console.log("--- Respuestas Extraídas ---");
console.log("q1 (Edad - Valor):", q1_respuesta);
console.log("q2 (Estudios - Valor):", q2_respuesta);
console.log("q3 (Capital Listo - Valor):", q3_respuesta);
console.log("q4 (Objetivo - Valor):", q4_respuesta);
console.log("q5 (Ingresos USD - Valor):", q5_respuesta);

if (!q1_respuesta || !q2_respuesta || !q3_respuesta || !q4_respuesta || !q5_respuesta) {
  console.warn("ADVERTENCIA: Una o más respuestas faltan. El puntaje se calculará con valor 0 para las faltantes.");
}

let puntajeTotal = 0;
console.log("--- Calculando Puntaje ---");
console.log("Puntaje inicial:", puntajeTotal);

switch (q1_respuesta) {
  case "a": puntajeTotal += 180; console.log("Puntos Edad (a): +180"); break; // Menor de 30 años
  case "b": puntajeTotal += 200; console.log("Puntos Edad (b): +200"); break; // 31 a 40 años
  case "c": puntajeTotal += 170; console.log("Puntos Edad (c): +170"); break; // 41 a 50 años
  case "d": puntajeTotal += 120; console.log("Puntos Edad (d): +120"); break; // 51 a 60 años
  case "e": puntajeTotal += 80;  console.log("Puntos Edad (e): +80"); break;  // 61 a 75 años
  default: console.log("Puntos Edad: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal después de Edad:", puntajeTotal);

switch (q2_respuesta) {
  case "a": puntajeTotal += 30;  console.log("Puntos Estudios (a): +30"); break; // Básica - Medios
  case "b": puntajeTotal += 50;  console.log("Puntos Estudios (b): +50"); break; // Secundaria
  case "c": puntajeTotal += 80;  console.log("Puntos Estudios (c): +80"); break; // Técnico
  case "d": puntajeTotal += 100; console.log("Puntos Estudios (d): +100"); break; // Tecnólogo
  case "e": puntajeTotal += 130; console.log("Puntos Estudios (e): +130"); break; // Profesional
  case "f": puntajeTotal += 150; console.log("Puntos Estudios (f): +150"); break; // Posgrado
  default: console.log("Puntos Estudios: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal después de Estudios:", puntajeTotal);

switch (q3_respuesta) {
  case "a": puntajeTotal += 250; console.log("Puntos Capital (a): +250"); break; // Sí, tengo ahorros para invertir.
  case "b": puntajeTotal += 120; console.log("Puntos Capital (b): +120"); break; // Estoy ahorrando para invertir en los próximos meses.
  case "c": puntajeTotal += 30;  console.log("Puntos Capital (c): +30"); break;  // No, por ahora no tengo capital para invertir.
  default: console.log("Puntos Capital: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal después de Capital:", puntajeTotal);

switch (q4_respuesta) {
  case "a": puntajeTotal += 200; console.log("Puntos Objetivo (a): +200"); break; // Tener una segunda fuente de ingresos
  case "b": puntajeTotal += 250; console.log("Puntos Objetivo (b): +250"); break; // Reemplazar mi trabajo actual
  case "c": puntajeTotal += 100; console.log("Puntos Objetivo (c): +100"); break; // Entender cómo invertir
  default: console.log("Puntos Objetivo: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal después de Objetivo:", puntajeTotal);

switch (q5_respuesta) {
  case "a": puntajeTotal += 20;  console.log("Puntos Ingresos (a): +20"); break; // 0 - 100 USD
  case "b": puntajeTotal += 40;  console.log("Puntos Ingresos (b): +40"); break; // 101 - 300 USD
  case "c": puntajeTotal += 80;  console.log("Puntos Ingresos (c): +80"); break; // 301 - 600 USD
  case "d": puntajeTotal += 120; console.log("Puntos Ingresos (d): +120"); break; // 1001 - 1500 USD
  case "e": puntajeTotal += 150; console.log("Puntos Ingresos (e): +150"); break; // +1500 USD
  default: console.log("Puntos Ingresos: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal antes de ajuste final:", puntajeTotal);

// --- 4. Ajuste Final y Retorno ---

puntajeTotal = Math.min(puntajeTotal, 1000);
puntajeTotal = Math.max(puntajeTotal, 0);

console.log("==============================");
console.log(" PUNTAJE FINAL CALCULADO:", puntajeTotal);
console.log("==============================");

return [{
  json: {
    puntaje: puntajeTotal
  }
}];
