// ---------- 1. PARSEO ROBUSTO ----------
let answersJsonOptions = {};
const inputData = $input.first();
const rawBody   = inputData.json.body;

function extractAnswers(body) {
  if (typeof body === 'string') return JSON.parse(body);
  if (body && typeof body === 'object') {
    if (body.answersJsonOptions)
      return typeof body.answersJsonOptions === 'string'
        ? JSON.parse(body.answersJsonOptions)
        : body.answersJsonOptions;
    if ('q1' in body) return body;
  }
  throw new Error('Formato de entrada no reconocido');
}

try { answersJsonOptions = extractAnswers(rawBody); }
catch (e) {
  return [{ json: { error: `Error procesando JSON: ${e.message}` } }];
}

// ---------- 2. RESPUESTAS ----------
const q1 = (answersJsonOptions.q1 || '').trim(); // Edad
const q2 = (answersJsonOptions.q2 || '').trim(); // Estudios
const q3 = (answersJsonOptions.q3 || '').trim(); // Experiencia uÃ±as
const q4 = (answersJsonOptions.q4 || '').trim(); // Objetivo
const q5 = (answersJsonOptions.q5 || '').trim(); // Ingresos

// ---------- 3. PUNTAJES ----------
const SCORE_TABLE = {
  q1: { a:  90, b:150, c:120, d: 75, e: 30 },
  q2: { a:   0, b: 20, c: 40, d: 60, e: 80, f:100 },
  q3: { a:  30, b: 75, c:150, d:120 },
  q4: { a: 350, b:280, c:140 },
  q5: { a:   0, b: 50, c:125, d:200, e:250 },
};

const get = (field, ans) => SCORE_TABLE[field]?.[ans] || 0;

// ---------- 4. TOTAL ----------
let total =
  get('q1', q1) +
  get('q2', q2) +
  get('q3', q3) +
  get('q4', q4) +
  get('q5', q5);

total = Math.min(Math.max(total, 0), 1000);

// ---------- 5. RETORNO ----------
return [{ json: { puntaje: total } }];
