const q4_respuesta = (answersJsonOptions["q4"] || "").toString().trim(); // Razón para aprender publicidad digital
const q5_respuesta = (answersJsonOptions["q5"] || "").toString().trim(); // Ingresos USD

// Depuración: Mostrar valores extraídos
console.log("--- Respuestas Extraídas ---");
