}
console.log("Subtotal después de Estudios:", puntajeTotal);

// Ocupación actual
switch (q3_respuesta) {
  case "a": puntajeTotal += 250; console.log("Puntos Ocupación (Negocio físico): +200"); break;
  case "b": puntajeTotal += 200; console.log("Puntos Ocupación (Marca personal): +180"); break;
  case "c": puntajeTotal += 100; console.log("Puntos Ocupación (Otro + interés online): +100"); break;
  case "d": puntajeTotal += 50; console.log("Puntos Ocupación (Aún no negocio): +120"); break;
  default: console.log("Puntos Ocupación: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal después de Ocupación:", puntajeTotal);

// Razón para aprender publicidad digital
switch (q4_respuesta) {
  case "a": puntajeTotal += 250; console.log("Puntos Publicidad (Aumentar ventas): +200"); break;
  case "b": puntajeTotal += 120; console.log("Puntos Publicidad (Ayudar a otros): +50"); break;
  case "c": puntajeTotal += 100; console.log("Puntos Publicidad (Habilidad extra): +150"); break;
  default: console.log("Puntos Publicidad: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal después de Publicidad:", puntajeTotal);

// Ingresos USD
switch (q5_respuesta) {
  case "a": puntajeTotal += 20;  console.log("Puntos Ingresos (0-100): +20"); break;
  case "b": puntajeTotal += 40;  console.log("Puntos Ingresos (101-300): +40"); break;
  case "c": puntajeTotal += 120;  console.log("Puntos Ingresos (301-600): +80"); break;
  case "d": puntajeTotal += 150; console.log("Puntos Ingresos (1001-1500): +120"); break;
  case "e": puntajeTotal += 180; console.log("Puntos Ingresos (+1500): +150"); break;
  default: console.log("Puntos Ingresos: +0 (Valor no reconocido o faltante)"); break;
}
console.log("Subtotal antes de ajuste final:", puntajeTotal);

// --- Ajuste Final y Retorno ---
puntajeTotal = Math.min(Math.max(puntajeTotal, 0), 1000);

console.log("==============================");
console.log(" PUNTAJE FINAL CALCULADO:", puntajeTotal);
console.log("==============================");

return [{
  json: {
    puntaje: puntajeTotal
  }
}];

