// Obtiene TODOS los items que llegan al nodo.
const items = $input.all();

// Itera sobre cada item para procesarlo.
for (const item of items) {

  // Extrae el valor de la respuesta del item actual.
  const respuestaProposito = item.json.Eliminar_dolor_para;

  // Variable para almacenar el resultado con un valor por defecto.
  let valorProposito = 'NO_ESPECIFICADO'; 

  // Asigna el valor a la variable PROPOSITO según la respuesta.
  switch (respuestaProposito) {
    case 'Para crear o ampliar tu negocio relacionado con la salud':
      valorProposito = 'NEGOCIO';
      break;
    case 'Para ti mismo':
      valorProposito = 'PERSONAL';
      break;
    case 'Para tu familia o amigos':
      valorProposito = 'FAMILIAR';
      break;
  }

  // Agrega el nuevo campo 'PROPOSITO' al objeto json del item.
  item.json.PROPOSITO = valorProposito;
}

// Retorna los items ya modificados.
return items;
