// Function Node (Run Once for All Items)

function to24h(h, m, s, ampmRaw) {
  let hh = parseInt(h, 10);
  const mm = parseInt(m, 10);
  const ss = parseInt(s, 10);
  const a = (ampmRaw || '').toLowerCase();
  const isPM = a.includes('p');
  const isAM = a.includes('a');
  if (isPM && hh < 12) hh += 12;
  if (isAM && hh === 12) hh = 0;
  return `${String(hh).padStart(2,'0')}:${String(mm).padStart(2,'0')}:${String(ss).padStart(2,'0')}`;
}

function clean(str) {
  return (str || '')
    .replace(/[\u202A-\u202E\u2066-\u2069\u200E\u200F]/g, '')
    .replace(/[\u00A0\u202F]/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

return items.map(({ json }) => {
  const rawOriginal = (json.NUMERO ?? '').toString();
  const raw = clean(rawOriginal);

  // Fecha/hora
  const dtRe = /^\[(\d{1,2})\/(\d{1,2})\/(\d{2}),\s*(\d{1,2}):(\d{2}):(\d{2})\s*([ap](?:\s*\.?\s*m\.?)?)\]/iu;
  const dt = raw.match(dtRe);
  if (!dt) {
    return { json: { error: 'No se pudo parsear fecha/hora', raw: rawOriginal } };
  }
  const dd = dt[1].padStart(2,'0');
  const mm = dt[2].padStart(2,'0');
  const yy = dt[3];
  const dia = `20${yy}-${mm}-${dd}`;
  const hora = to24h(dt[4], dt[5], dt[6], dt[7]);

  // Teléfono: último bloque con forma de teléfono
  const phoneBlocks = raw.match(/[+]?\d[\d()\s\-\u2010-\u2015\u2212]+/g);
  if (!phoneBlocks || phoneBlocks.length === 0) {
    return { json: { error: 'No se encontró ningún número', raw: rawOriginal } };
  }
  const lastPhone = phoneBlocks[phoneBlocks.length - 1];
  const whatsapp = lastPhone.replace(/\D/g, '');

  // Campos adicionales
  const grupo = json.grupo ?? '120363421901279116@g.us';
  const nombre_grupo = json.col_2 ?? json.nombre_grupo ?? 'Desconocido';
  const fuente = json.fuente ?? 'FB ADS';
  const id = json.row_number ?? (Date.now() % 1e9);

  return {
    json: {
      dia,
      hora,
      whatsapp,
      grupo,
      nombre_grupo,
      fuente
    }
  };
});
